pragma solidity ^0.8.0;

contract PollingSys {
    struct Option {
        uint8 id;
        string name;
        uint voteCount;
    }

    struct Voter {
        bool isRegistered;
        bool hasVoted;
        uint8 vote;
    }

    struct Poll {
        uint8 id;
        address author;
        string title;
        uint8 optionsCount;
        mapping(uint8 => Option) options;
        mapping(address => Voter) voters;
        bytes32 accessCodeHash;
        PHASE state;
    }

    enum PHASE {
        reg,
        voting,
        done
    }

    uint8 public pollCount;
    mapping(uint8 => Poll) public polls;

    event PollCreated(uint8 pollId, address author, string title);
    event OptionAdded(uint8 pollId, uint8 optionId, string name);
    event Voted(uint8 pollId, address voter, uint8 optionId);

    modifier onlyAuthor(uint8 _pollId) {
        require(msg.sender == polls[_pollId].author, "Only author can manage this poll");
        _;
    }

    modifier validState(uint8 _pollId, PHASE x) {
        require(polls[_pollId].state == x, "Invalid poll state");
        _;
    }

    function createPoll(string memory _title, string memory _accessCode) public {
        pollCount++;
        Poll storage newPoll = polls[pollCount];
        newPoll.id = pollCount;
        newPoll.author = msg.sender;
        newPoll.title = _title;
        newPoll.accessCodeHash = keccak256(abi.encodePacked(_accessCode));
        newPoll.state = PHASE.reg;

        emit PollCreated(pollCount, msg.sender, _title);
    }

    function addOptionsToPoll(uint8 _pollId, string memory _name) 
        public onlyAuthor(_pollId) validState(_pollId, PHASE.reg) 
    {
        Poll storage poll = polls[_pollId];
        poll.optionsCount++;
        poll.options[poll.optionsCount] = Option(poll.optionsCount, _name, 0);

        emit OptionAdded(_pollId, poll.optionsCount, _name);
    }

    function vote(uint8 _pollId, uint8 _optionId, string memory _accessCode) 
        public validState(_pollId, PHASE.voting) 
    {
        Poll storage poll = polls[_pollId];
        Voter storage voter = poll.voters[msg.sender];

        require(!voter.hasVoted, "Voter has already voted");
        require(keccak256(abi.encodePacked(_accessCode)) == poll.accessCodeHash, "Invalid access code");
        require(_optionId > 0 && _optionId <= poll.optionsCount, "Invalid option");

        poll.options[_optionId].voteCount++;
        voter.hasVoted = true;
        voter.vote = _optionId;

        emit Voted(_pollId, msg.sender, _optionId);
    }

    function getOption(uint8 _pollId, uint8 _optionId) 
        public view returns (uint8, string memory, uint) 
    {
        Option storage option = polls[_pollId].options[_optionId];
        return (option.id, option.name, option.voteCount);
    }
}
